import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = () => {
    setShowMenu((prevShowMenu) => !prevShowMenu);
  };

  return (
    <header className="header">
      <div className="logo-container">
        <img src="/images/electrovolt.jpg" alt="Company Logo" className="logo" />
      </div>
      <nav className={`navigation ${showMenu ? 'show' : ''}`}>
        <div className="menu-toggle" onClick={toggleMenu}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </div>
        <div className="nav-links">
          <Link to="/about">About Us</Link>
          <div className="dropdown">
            <span className="dropdown-title">Solutions</span>
            <div className="dropdown-content">
              <Link to="/carcharge">Car Charging</Link>
              <Link to="/buscharge">Bus Charging</Link>
              <Link to="/truckcharge">Truck Charging</Link>
            </div>
          </div>
          <Link to="/charger-locator">Charger Locator</Link>
          <Link to="/contact">Contact Us</Link>
          <Link to="/">Home</Link>
        </div>
      </nav>
    </header>
  );
};

export default Header;
