// AboutUs.js
// import React from 'react';
import './About.css'; // Create a corresponding CSS file
import React,{useEffect} from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';

const About = () => {
  useEffect(() => {
    AOS.init({
      duration: 1000, // Set the duration of the animation
      offset: 100,
      once: false, // Only animate once
    });
  }, []);

  return (

    <div className="about-us">
      <section className="mission-section" data-aos="fade-up">
        <div className="section-content">
          <h2>Mission Statement</h2>
          <p>
            Our mission is to provide sustainable and innovative solutions to meet the evolving needs of our customers.
            We are dedicated to making a positive impact on the environment and the communities we serve.
          </p>
        </div>
      </section>

      <section className="company-info-section" data-aos="fade-up">
        <div className="section-content">
          <h2>ElectroVolt</h2>
          <p>
          ElectroVolt spearheads the electric vehicle revolution, 
          pioneering cutting-edge charging solutions tailored for a 
          diverse spectrum of electric vehicles, ranging from cars 
          to buses and trucks. Our dedication to providing reliable 
          charging solutions is unwavering, driven by a commitment 
          to shaping the future of sustainable transportation. 
          Our strategically positioned charging stations redefine 
          convenience, instilling peace of mind that empowers you 
          to drive without fear. Experience the seamless integration 
          of advanced technology with a dependable charging network, 
          as we pave the way for a greener and more efficient future
           in electric mobility.
          </p>
        </div>
      </section>

      <section className="founder-info-section" data-aos="fade-up">
        <div className="section-content">
          <h2>Founder Information</h2>
          <p>
            Meet our visionary founder, [Founder Name], who established the company with a passion for innovation and a
            commitment to excellence. Learn more about the journey and values that guide our organization.
          </p>
        </div>
      </section>
    </div>
  );
};

export default About;
