// src/components/ContactUs.js
import React, { useState } from 'react';
import Header from './Header';
import './ContactUs.css'; // Create a CSS file for styling if necessary

const ContactUs = () => {
  const [showDialog, setShowDialog] = useState(true); // Set to true by default
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    fetch('http://localhost:3001/send-email', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Success:', data);
        // Handle success (e.g., show a success message)
      })
      .catch((error) => {
        console.error('Error:', error);
        // Handle error (e.g., show an error message)
      });

    // Clear the form and close the dialog
    setFormData({
      name: '',
      email: '',
      phone: '',
      subject: '',
      message: '',
    });

  };

  return (
    <div>
      <Header />
      <div className={`home-container ${showDialog ? 'show' : ''}`}>
        <div className={`main-content ${showDialog ? 'show' : ''}`}>
          <h2>Contact Us Page</h2>
          <div className={`dialog ${showDialog ? 'show' : ''}`}>
            <h1>Send us a Message</h1>
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" name="name" value={formData.name} onChange={handleInputChange} required />

            <label htmlFor="email">Email:</label>
            <input type="email" id="email" name="email" value={formData.email} onChange={handleInputChange} required />

            <label htmlFor="phone">Phone:</label>
            <input type="tel" id="phone" name="phone" value={formData.phone} onChange={handleInputChange} required />

            <label htmlFor="subject">Subject:</label>
            <input type="text" id="subject" name="subject" value={formData.subject} onChange={handleInputChange} required />

            <label htmlFor="message">Message:</label>
            <textarea id="message" name="message" value={formData.message} onChange={handleInputChange} required />

            <button onClick={handleSubmit}>Send Message</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContactUs;
