// src/components/AboutUs.js
import React from 'react';
import './AboutUs.css'; // Create a CSS file for styling if necessary

const AboutUs = () => {
  return (
    <div>

        <div className="about-us-container">
        <div className="about-us-content">
            <h2>About ElectroVolt</h2>
            <p>
            ElectroVolt is a leading company in the field of electric vehicles and sustainable energy solutions.
            We are committed to providing innovative and eco-friendly charging solutions to make the world a
            greener and cleaner place.
            </p>
            <p>
            Our mission is to revolutionize the way people think about transportation and energy consumption.
            With cutting-edge technology and a passion for sustainability, ElectroVolt is driving the future
            of electric mobility.
            </p>
        </div>
        </div>
    </div>

  );
};

export default AboutUs;
