import React,{useEffect} from 'react';
import './Home.css';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Home = () => {
  useEffect(() => {
    AOS.init({
      duration: 1000, // Set the duration of the animation
      offset: 100,
      once: false, // Only animate once
    });
  }, []);
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };
  const images = [
    'your-image1.jpg', // Replace with the actual path to your first image
    'your-image2.jpg', // Replace with the actual path to your second image
    // Add more images as needed
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };
  return (
    <div>
      {/* home page */}
      <section id="home" >
          <div className="slideshow-container">
            {images.map((image, index) => (
              <img
                key={index}
                src={image}
                alt={`Image ${index + 1}`}
                className={`slide ${index === currentImageIndex ? 'visible' : 'hidden'}`}
              />
            ))}
            <button className="prev" onClick={handlePrevImage}>&#10094;</button>
            <button className="next" onClick={handleNextImage}>&#10095;</button>
          </div>
      </section>

    {/* about page */}
      <section id="about" >

      </section>

      {/* products page */}
      <section id="products" >


      </section>

    {/* values page */}
      <section id="values" >

      </section>

    {/* contact page */}
      <section id="contact">

      </section>

    </div>
  );
};

export default Home;
