import React,{useEffect,useState} from 'react';
import './Home.css';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Home = () => {
  useEffect(() => {
    AOS.init({
      duration: 1000, // Set the duration of the animation
      offset: 100,
      once: false, // Only animate once
    });
  }, []);
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };
  const images = [
    '/images/TRUCK.png', 
    '/images/CHARGER.png', 
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div>
      {/* home page */}
      <section id="home" className="home-section">
        <div className="w3-content w3-display-container">
            {images.map((image, index) => (
              <img
                key={index}
                className={`mySlides fade ${index === currentImageIndex ? 'visible' : 'hidden'}`}
                src={image}
                alt={`Image ${index + 1}`}
              />
            ))}
            <button className="prev" onClick={handlePrevImage}>&#10094;</button>
            <button className="next" onClick={handleNextImage}>&#10095;</button>
            <div className="w3-center w3-display-bottommiddle" style={{ width: '100%' }}>
              {images.map((_, index) => (
                <span
                  key={index}
                  className={`w3-badge demo w3-border ${index === currentImageIndex ? 'active' : ''}`}
                  onClick={() => setCurrentImageIndex(index)}
                ></span>
              ))}
            </div>
          </div>
      </section>

    {/* about page */}
      <section id="about" >
        <div className="about-content" data-aos="fade-up">
          <h2>About Us</h2>
          <p>

Mittal Food stands as a preeminent manufacturing powerhouse specializing in the production 
of premium-quality dough sheets in India. Our corporate headquarters is strategically located in the vibrant town of Pardi, nestled within the picturesque District of Valsad in Gujarat. Leveraging a wealth of industry expertise, we take immense pride in our 15-year legacy, during which we have consistently demonstrated a commitment to excellence in both dough sheet manufacturing and overall product quality.


          </p>

        </div>
      </section>

      {/* products page */}
      <section id="products" >


      </section>

    {/* values page */}
      <section id="values" >

      </section>

    {/* contact page */}
      <section id="contact">

      </section>

    </div>
  );
};

export default Home;
