// ContactUs.js
import React, { useState } from 'react';
import './Contact.css'; // Create a corresponding CSS file

const Contact = () => {
  const [showDialog, setShowDialog] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/send-email', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });

    setFormData({
      name: '',
      email: '',
      phone: '',
      subject: '',
      message: '',
    });
  };

  return (
    <div className="contact-us">
      <div className="contact-info">
      <h2>Contact Information</h2>
        {/* <img
            className="imgdets"
            src="/images/contactus.jpg" 
            alt="Contact Image"
          /> */}
       {/* <h3>Address: </h3> */}
        <p>
          Address:<br/> Electrovolt, 305, <br />
          Third Floor, Centre Point,<br />
          Opp. Bai Avabai High School, 396001<br />
          Valsad, Gujarat, India<br />
          Phone: +91 9687558299<br />
          <div className="mail-box">
                    Email: 
                    <p>info@example.com</p>
                    <button onClick={() => window.open('mailto:info_hub@electrovolt.com')}>Send Email</button>
          </div>
        </p>
      </div>
      <div className="contact-form">
        <h2>Send us a Message!</h2>
        <div className={`dialog ${showDialog ? 'show' : ''}`}></div>
        <form>
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" name="name" value={formData.name} onChange={handleInputChange} required />

          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" value={formData.email} onChange={handleInputChange} required />


          <label htmlFor="phone">Phone:</label>
          <input type="tel" id="phone" name="phone" value={formData.phone} onChange={handleInputChange} required />

          <label htmlFor="subject">Subject:</label>
          <input type="text" id="subject" name="subject" value={formData.subject} onChange={handleInputChange} required />

          <label htmlFor="message">Message:</label>
          <textarea id="message" name="message" value={formData.message} onChange={handleInputChange} required />

          <button type="submit" onClick={handleSubmit}>Send Message</button>
        </form>
      </div>
    </div>
  );
};

export default Contact;
