// src/components/ContactUs.js
import React, { useState } from 'react';
import Header from './Header';
import './ContactUs.css';

const ContactUs = () => {
  const [showDialog, setShowDialog] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/send-email', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });

    setFormData({
      name: '',
      email: '',
      phone: '',
      subject: '',
      message: '',
    });
  };

  return (
    <div>
      <Header />
      {/* Additional boxes below the header */}
      <div className="contact-info">
        <div className="left-info">
          <div className="address-box">
            <h3>On-site Address</h3>
            <p>123 Main St, Cityville, Country</p>
          </div>

          <div className="on-call-box">
            <h3>On-call</h3>
            <p>Phone: +1 123-456-7890</p>
          </div>
        </div>

        <div className="right-info">
          <div className="whatsapp-box">
            <h3>WhatsApp</h3>
            <p>Chat: +1 123-456-7890</p>
            <button onClick={() => window.open('https://wa.me/11234567890', '_blank')}>Call</button>
          </div>

          <div className="mail-box">
            <h3>Email</h3>
            <p>electrovolt-info@example.com</p>
            <button onClick={() => window.open('mailto:info@example.com')}>Send Email</button>
          </div>
        </div>
      </div>

      {/* Main content */}
      <div className={`home-container ${showDialog ? 'show' : ''}`}>
        <div className={`main-content ${showDialog ? 'show' : ''}`}>
          <h2>Contact Us Page</h2>
          <div className={`dialog ${showDialog ? 'show' : ''}`}>
            <h1>Send us a Message</h1>
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" name="name" value={formData.name} onChange={handleInputChange} required />

            <label htmlFor="email">Email:</label>
            <input type="email" id="email" name="email" value={formData.email} onChange={handleInputChange} required />

            <label htmlFor="phone">Phone:</label>
            <input type="tel" id="phone" name="phone" value={formData.phone} onChange={handleInputChange} required />

            <label htmlFor="subject">Subject:</label>
            <input type="text" id="subject" name="subject" value={formData.subject} onChange={handleInputChange} required />

            <label htmlFor="message">Message:</label>
            <textarea id="message" name="message" value={formData.message} onChange={handleInputChange} required />

            <button onClick={handleSubmit}>Send Message</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContactUs;









